caddy.caddyfile_snippet:
# VAPT RISK-002: Xmlrpc enabled Leads to Ping back attack
@blocked_file {
    path */xmlrpc.php
}
respond @blocked_file 403

# VAPT RISK-003: Username Enumeration via WordPress REST API
@rest_users {
    path /wp-json/wp/v2/users*
}
respond @rest_users 403

# VAPT RISK-005: Exposed WordPress Admin Username via Author Query
@author_enum {
    query author=*
}
respond @author_enum 403

# VAPT RISK-011: Information Disclosure via readme.html
@blocked_file {
    path */readme.html
}
respond @blocked_file 403

# VAPT RISK-012: HSTS Not Implemented
header {
    Strict-Transport-Security "max-age=31536000; includeSubDomains; preload"
    defer
}

# VAPT RISK-013: Directory Listing Enabled
# Directory listing disabled by default in Caddy's file_server
# Explicitly set browse to off:
file_server {
    # browse directive omitted = directory listing disabled
}

# VAPT RISK-014: Missing X-Frame-Options Header
header {
    X-Frame-Options "SAMEORIGIN"
    defer
}

# VAPT RISK-015: Missing X-Content-Type-Options Header
header {
    X-Content-Type-Options "nosniff"
    defer
}

# VAPT RISK-016: Missing X-XSS-Protection Header
header {
    X-XSS-Protection "1; mode=block"
    defer
}

# VAPT RISK-017: Missing Referrer-Policy Header
header {
    Referrer-Policy "strict-origin-when-cross-origin"
    defer
}

# VAPT RISK-018: Missing Permissions-Policy Header
header {
    Permissions-Policy "geolocation=(), microphone=(), camera=()"
    defer
}

# VAPT RISK-019: Sensitive Files Accessible
@blocked_file {
    path */\.(env|git|sql|log)$
}
respond @blocked_file 403

# VAPT RISK-021: WordPress Config File Accessible
@blocked_file {
    path */wp-config.php
}
respond @blocked_file 403

# VAPT RISK-022: Missing Content-Security-Policy Header
header {
    Content-Security-Policy "default-src 'self'; script-src 'self' 'unsafe-inline"
    defer
}

# VAPT RISK-023: Trace Method Enabled
@trace_method {
    method TRACE
}
respond @trace_method 405

# VAPT RISK-024: Server Signature Enabled
header {
    -Server
}

# VAPT RISK-025: ETags Information Leakage
header {
    -ETag
}

# VAPT RISK-027: WordPress Install File Accessible
@blocked_file {
    path */install.php
}
respond @blocked_file 403

# VAPT RISK-028: WordPress Upgrade File Accessible
@blocked_file {
    path */upgrade.php
}
respond @blocked_file 403

# VAPT RISK-029: Backup Files Accessible
@blocked_file {
    path */\.(bak|backup|old|orig|save)$
}
respond @blocked_file 403

# VAPT RISK-030: Log Files Accessible
@blocked_file {
    path */\.(log|error)$
}
respond @blocked_file 403

# VAPT RISK-031: Missing Cross-Origin-Resource-Policy
header {
    Cross-Origin-Resource-Policy "same-origin"
    defer
}

# VAPT RISK-032: Missing Cross-Origin-Embedder-Policy
header {
    Cross-Origin-Embedder-Policy "require-corp"
    defer
}

# VAPT RISK-033: Missing Cross-Origin-Opener-Policy
header {
    Cross-Origin-Opener-Policy "same-origin"
    defer
}

# VAPT RISK-034: WordPress Debug Log Exposed
@blocked_file {
    path */debug.log
}
respond @blocked_file 403
