caddy.caddyfile_snippet:
# VAPT RISK-002: Xmlrpc enabled Leads to Ping back attack
@blocked_file {
    path */xmlrpc.php
}
respond @blocked_file 403

# BEGIN VAPT RISK-003: Username Enumeration via WordPress REST API
@risk003 {
    path /wp-json/wp/v2/users*
}
respond @risk003 "Access Denied" 403
# END VAPT RISK-003

# BEGIN VAPT RISK-005: Exposed WordPress Admin Username via Author Query
@risk005 {
    query author=*
}
respond @risk005 "Access Denied" 403
# END VAPT RISK-005

# VAPT RISK-011: Information Disclosure via readme.html
@blocked_file {
    path */readme.html
}
respond @blocked_file 403

# BEGIN VAPT RISK-012: HSTS Not Implemented
header {
    Strict-Transport-Security "max-age=31536000; includeSubDomains; preload"
    defer
}
# END VAPT RISK-012

# BEGIN VAPT RISK-014: Missing X-Frame-Options Header
header {
    X-Frame-Options "SAMEORIGIN"
    defer
}
# END VAPT RISK-014

# BEGIN VAPT RISK-015: Missing X-Content-Type-Options Header
header {
    X-Content-Type-Options "nosniff"
    defer
}
# END VAPT RISK-015

# BEGIN VAPT RISK-016: Missing X-XSS-Protection Header
header {
    X-XSS-Protection "1; mode=block"
    defer
}
# END VAPT RISK-016

# BEGIN VAPT RISK-017: Missing Referrer-Policy Header
header {
    Referrer-Policy "strict-origin-when-cross-origin"
    defer
}
# END VAPT RISK-017

# BEGIN VAPT RISK-018: Missing Permissions-Policy Header
header {
    Permissions-Policy "geolocation=(), microphone=(), camera=()"
    defer
}
# END VAPT RISK-018

# BEGIN VAPT RISK-020: PHP File Execution in Uploads Directory
@blocked_risk020 {
    path *.php
}
respond @blocked_risk020 "Access Denied" 403
# END VAPT RISK-020

# BEGIN VAPT RISK-021: WordPress Config File Accessible
@blocked_risk021 {
    path /wp-config.php
}
respond @blocked_risk021 "Access Denied" 403
# END VAPT RISK-021

# BEGIN VAPT RISK-022: Missing Content-Security-Policy Header
header {
    Content-Security-Policy "default-src 'self'; script-src 'self' 'unsafe-inline'; style-src 'self' 'unsafe-inline'; img-src 'self' data: https:; font-src 'self'; connect-src 'self'; frame-ancestors 'none';"
    defer
}
# END VAPT RISK-022

# BEGIN VAPT RISK-023: Trace Method Enabled
@risk023 {
    method TRACE
}
respond @risk023 "Access Denied" 403
# END VAPT RISK-023

# BEGIN VAPT RISK-025: ETags Information Leakage
header {
    -ETag
    defer
}
# END VAPT RISK-025

# BEGIN VAPT RISK-027: WordPress Install File Accessible
@blocked_risk027 {
    path /install.php
}
respond @blocked_risk027 "Access Denied" 403
# END VAPT RISK-027

# BEGIN VAPT RISK-028: WordPress Upgrade File Accessible
@blocked_risk028 {
    path /upgrade.php
}
respond @blocked_risk028 "Access Denied" 403
# END VAPT RISK-028

# BEGIN VAPT RISK-030: Log Files Accessible
@blocked_risk030 {
    path *.log
}
respond @blocked_risk030 "Access Denied" 403
# END VAPT RISK-030

# BEGIN VAPT RISK-031: Missing Cross-Origin-Resource-Policy
header {
    Cross-Origin-Resource-Policy "same-origin"
    defer
}
# END VAPT RISK-031

# BEGIN VAPT RISK-032: Missing Cross-Origin-Embedder-Policy
header {
    Cross-Origin-Embedder-Policy "require-corp"
    defer
}
# END VAPT RISK-032

# BEGIN VAPT RISK-033: Missing Cross-Origin-Opener-Policy
header {
    Cross-Origin-Opener-Policy "same-origin"
    defer
}
# END VAPT RISK-033

# BEGIN VAPT RISK-034: WordPress Debug Log Exposed
@blocked_risk034 {
    path /debug.log
}
respond @blocked_risk034 "Access Denied" 403
# END VAPT RISK-034

# BEGIN VAPT RISK-035: Apache Status Page Accessible
@risk035 {
    path /server-status* /server-info*
}
respond @risk035 "Access Denied" 403
# END VAPT RISK-035

